const cardContainer=document.querySelector(".cardContainer"),card=document.querySelector(".cardContainer .card"),pruchasebtns=document.querySelectorAll(".cardContainer .card .info .sizes button"),cardTitle=document.querySelector(".cardContainer .card .info .title"),cardSneaker=document.querySelector(".cardContainer .card .sneaker img"),cardPurchase=document.querySelector(".cardContainer .card .info .purchase button"),cardDiscription=document.querySelector(".cardContainer .card .info .about"),cardsizes=document.querySelector(".cardContainer .card .info .sizes");function addActive(){pruchasebtns.forEach(e=>{e.classList.remove("active")})}cardContainer.addEventListener("mousemove",e=>{let r=(window.innerWidth/2-e.pageX)/15,t=(window.innerHeight/2-e.pageY)/15;card.style.transform=`rotateY(${r}deg) rotateX(${t}deg)`}),cardContainer.addEventListener("mouseenter",e=>{card.style.transition="none",cardTitle.style.transform="translateZ(70px)",cardSneaker.style.transform="translateZ(100px) rotateZ(-45deg)",cardPurchase.style.transform="translateZ(40px)",cardDiscription.style.transform="translateZ(60px)",cardsizes.style.transform="translateZ(50px)"}),cardContainer.addEventListener("mouseleave",e=>{card.style.transition=" all 0.2s ease-in-out",card.style.transform="rotateY(0deg) rotateX(0deg)",cardTitle.style.transform="translateZ(0px)",cardSneaker.style.transform="translateZ(0px) rotateZ(0deg)",cardPurchase.style.transform="translateZ(0px)",cardDiscription.style.transform="translateZ(0px)",cardsizes.style.transform="translateZ(0px)"}),pruchasebtns.forEach(e=>{e.addEventListener("click",function(){addActive(),e.classList.add("active")})});